}

.gear-card-header {
    display: flex;
    align-items: center;
    gap: 10px;
    margin-bottom: 12px;
    padding-bottom: 10px;
    border-bottom: 1px solid var(--border-subtle);
}

.gear-card-icon {
    font-size: 1.2rem;
}

.gear-card-title {
