}

.gear-stat-label {
    font-size: 0.7rem;
    color: var(--text-muted);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.gear-stat-value {
    font-size: 0.9rem;
    color: var(--text-cream);
    font-weight: 600;
}

.gear-stat-value.stat-good {
