    padding: 0 20px;
}

.hero-panel-inner {
    display: grid;
    grid-template-columns: 240px 1fr;
    gap: 26px;
    padding: 26px;
    background: linear-gradient(180deg, rgba(20,20,20,0.98), rgba(12,12,12,0.98));
    border: 1px solid var(--border-medium);
    border-radius: 10px;
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.05), 0 12px 28px rgba(0,0,0,0.55);
