    margin: 0;
    padding: 0;
    background: none;
}

.hero-panel .section-nav::before,
.hero-panel .section-nav::after {
    display: none;
}

.hero-panel .section-nav-inner {
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 0;
}

.hero-panel .section-nav-item {
    border-radius: 0;
}

/* Welcome side adjustments */
.hero-panel .welcome-message {
    text-align: left;
    padding: 0;
    border: none;
    background: none;
    box-shadow: none;
}

.hero-panel .welcome-message h2 {
    text-align: left;
    margin-bottom: 8px;
}

.hero-panel .welcome-message h3 {
    text-align: left;
    margin: 16px 0 12px;
}

.hero-panel .welcome-hint {
    text-align: left;
}

/* Example queries as bounty cards */
.hero-panel .example-queries {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 10px;
    max-width: none;
    margin: 0;
}

.hero-panel .example-queries li {
    background: linear-gradient(180deg, #1e1a16, #14120f);
    border: 1px solid #3a2e22;
    border-left: 3px solid var(--rust);
    padding: 10px 14px;
    text-align: left;
    font-size: 0.9rem;
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.04), 0 4px 10px rgba(0,0,0,0.4);
